import { View, StatusBar } from "react-native";
import {
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  Image,
  ActivityIndicator,
  Animated,
  Switch,
} from "react-native";
import { Boton } from "../../Style/Boton";
import { useState, useRef, useCallback } from "react";
import ImagePikker from "../ProfileScreens/ImagePikker";
import { firebase } from "../firebaseConfig/Config";
import { Ionicons } from "@expo/vector-icons";
import { ShowError, ShowSuccess } from "../../Style/ErrorMessage";
import RNPickerSelect from 'react-native-picker-select';

export const SignUp = ({ navigation }) => {
  // const co0lor="green"
  const [isEnabled, setIsEnabled] = useState(false);
  const toggleSwitch = () => setIsEnabled((previousState) => !previousState);

  const [fullName, setFullname] = useState("");
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("");
  const [cpass, setCPass] = useState("");
  const [loading, setloading] = useState(false);
  const [Address, setAddress] = useState("");
  const [phone, setPhone] = useState("");

  const [eye, setEye] = useState("eye-off");
  const [visible, setVisible] = useState(true);
  const [eye2, setEye2] = useState("eye-off");
  const [visible2, setVisible2] = useState(true);
  const [color, setColor] = useState("gray");
  const [color2, setColor2] = useState("gray");
  const [imge, setImge] = useState("");

  const [fnValid, setFnValid] = useState(null);
  const [lnValid, setLnValid] = useState(null);
  const [emailValid, setEmailValid] = useState(null);
  const [passValid, setPassValid] = useState(null);
  const [cpassValid, setCPassValid] = useState(null);
  const [isValid, setIsValid] = useState(false);
  const emailRegex = /\S+@\S+\.\S+/;

  const [placeholder,setPlaceHolder]=useState({
    label: 'Select Your Skills...',
    value: null,
    color: '#9EA0A4',
  })
  
  const anim = useRef(new Animated.Value(0));

  const shake = useCallback(() => {
    // makes the sequence loop
    Animated.loop(
      // runs the animation array in sequence
      Animated.sequence([
        // shift element to the left by 2 units
        Animated.timing(anim.current, {
          toValue: -10,
          duration: 50,
          useNativeDriver: true,
        }),
        // shift element to the right by 2 units
        Animated.timing(anim.current, {
          toValue: 10,
          duration: 50,
          useNativeDriver: true,
        }),
        // bring the element back to its original position
        Animated.timing(anim.current, {
          toValue: 0,
          duration: 50,
          useNativeDriver: true,
        }),
      ]),
      // loops the above animation config 2 times
      { iterations: 2 }
    ).start();
  }, []);

  const showpass = () => {
    if (eye == "eye-off") {
      setEye("eye");
      setColor("green");
      setVisible(false);
    } else if (eye == "eye") {
      setEye("eye-off");
      setColor("gray");
      setVisible(true);
    }
  };
  const showpass2 = () => {
    if (eye2 == "eye-off") {
      setEye2("eye");
      setColor2("green");
      setVisible2(false);
    } else if (eye2 == "eye") {
      setEye2("eye-off");
      setColor2("gray");
      setVisible2(true);
    }
  };
  if (loading) {
    return (
      <View style={{ justifyContent: "center", flex: 1 }}>
        <ActivityIndicator size="large" color="#00ff00" />
      </View>
    );
  }

  const validateEmail = () => {
    if (emailRegex.test(email)) {
      setIsValid(true);
    } else {
      setIsValid(false);
      setEmailValid("Please enter a valid email!");
    }
  };
  const CreateUserAccount = () => {
    if (email == "" && pass == "" && fullName == "" && cpass == "") {
      ShowError("Please fill out all required fields");
      shake();
      return false;
    }

    if (fullName == "" || email == "" || pass == "" || cpass == "") {
      if (fullName == "") {
        //     setFnValid("*Required");

        shake();
        ShowError("Please inter your Fullname");
        return false;
      }
      if (email == "") {
        //   setEmailValid("*Required");
        shake();
        ShowError("Please inter your Email");
        return false;
      }

