import React from "react";
import { View, TextInput, Text, Image } from "react-native";
import { useState } from "react";
import { Boton } from "../../Style/Boton";
import { Ionicons } from "@expo/vector-icons";
import { firebase } from "../firebaseConfig/Config";
import { ShowError, ShowSuccess } from "../../Style/ErrorMessage";
export const ForgotPassword = ({ navigation }) => {
  const [email, setEmail] = useState("");
  const [valid1, setValid1] = useState(null);
  const [isValid, setIsValid] = useState(false);
  const [visib, setVisib] = useState(false);
  const [title, setTitle] = useState("Next");
  const emailRegex = /\S+@\S+\.\S+/;

  // const [color2, setColor2] = useState('gray');
  // const [pass2, setPass2] = useState("");
  // const [valid2, setValid2] = useState(null);
  // const [eye2, setEye2] = useState("eye-off");
  // const [visible2, setVisible2] = useState(true);

  // const [color3, setColor3] = useState('gray');
  // const [pass3, setPass3] = useState("");
  // const [valid3, setValid3] = useState(null);
  // const [eye3, setEye3] = useState("eye-off");
  // const [visible3, setVisibl3] = useState(true);
  const changeState = () => {
    if (email == "") {
      ShowError("Please inter your Email");
      return false;
    } else {
      firebase
        .auth()
        .sendPasswordResetEmail(email)
        .then(function () {
          // Email sent.
          console.log("send email");
          navigation.goBack();
          ShowSuccess("Send Link to your email.");
          return false;
        })
        .catch((error) => {
          ShowError(error.toString());
          console.log("not send");
          return false;
        });

      setTitle("Update");
    }
  };

  const showpass2 = () => {
    if (eye2 == "eye-off") {
      setEye2("eye");
      setColor2("green");
      setVisible2(false);
    } else if (eye2 == "eye") {
      setEye2("eye-off");
      setColor2("gray");
      setVisible2(true);
    }
  };
  const showpass3 = () => {
    if (eye3 == "eye-off") {
      setEye3("eye");
      setColor3("green");
      setVisibl3(false);
    } else if (eye3 == "eye") {
      setEye3("eye-off");
      setColor3("gray");
      setVisibl3(true);
    }
  };
  const validateEmail = () => {
    if (emailRegex.test(email)) {
      setIsValid(true);
    } else {
      setIsValid(false);
      setValid1("Please enter a valid email!");
    }
  };

  const forgotPassword = () => {};

  return (
    <View>
      <View style={{ alignItems: "center" }}>
        <Image
          style={{ width: 90, height: 90, marginTop: 100 }}
          source={require("../../assets/EXtraUsepictures/email2.png")}
        />
      </View>
      {/* {visib === false ? */}
      <View
        style={{
          paddingLeft: 30,
          paddingRight: 30,
          paddingBottom: 30,
          paddingTop: 100,
        }}
      >
        <TextInput
          style={{
            fontSize: 16,
            color: "black",
            borderBottomColor: email == "" ? "#ddd" : "green",
            borderBottomWidth: 1,
            paddingBottom: 10,
          }}
          placeholder="Enter Email"
          keyboardType="email-address"
          onChangeText={(t1) => {
            setEmail(t1);
            setValid1("");
          }}
          onChange={validateEmail}
        />
        <Text style={{ color: "red" }}>{valid1}</Text>
      </View>
      {/* : */}
      {/* <View style={{ marginTop: 70 }}>
            <View style={{paddingLeft:30,paddingRight:30,paddingBottom:30}}>
                <TextInput
                style={{ fontSize: 16, color: 'black', borderBottomColor:pass2==""?"#ddd":"green", borderBottomWidth:1,paddingBottom:10}}
                    placeholder="New Password"
                    secureTextEntry={visible2}
                        onChangeText={t2 => {
                            setPass2(t2)
                            setValid2(""); }}
                />

                <View style={{marginLeft:285,marginTop:-35}}>
                    <Ionicons onPress={showpass2} name={eye2} size={28} color={color2} />
                </View>
                
                    <Text style={{color:"red"}}>{valid2}</Text>
            </View>
            <View style={{paddingLeft:30,paddingRight:30,paddingBottom:30}}>
                <TextInput
                style={{ fontSize: 16, color: 'black', borderBottomColor:pass3==""?"#ddd":"green", borderBottomWidth:1,paddingBottom:10}}
                    placeholder="Retype New Password"
                    secureTextEntry={visible3}
                        onChangeText={t2 => {
                            setPass3(t2)
                            setValid3(""); }}
                />

                <View style={{marginLeft:285,marginTop:-35}}>
                    <Ionicons onPress={showpass3} name={eye3} size={28} color={color3} />
                </View>

                </View>
            </View> */}
      {/* } */}
      <View style={{ alignItems: "center", marginTop: 50 }}>
        <Boton widt={150} title={"Send Link"} onPress={changeState} />
      </View>
    </View>
  );
};
