import React, { useEffect, useState } from "react";
import { FlatList, SafeAreaView, StatusBar, StyleSheet, Text, TouchableOpacity,Image,View } from "react-native";
import { DATA } from "../../Style/DATA";
import { Rating, AirbnbRating } from 'react-native-ratings';

let coloure=''
const ratingCompleted=(rating)=>{
  console.log("Rating is: " + rating)
}
  

export const HelperListNearbye = ({ navigation,route}) => {
  // const { name } = route.params;
  // console.log(name)
  const Item = ({ item, onPress, backgroundColor, textColor }) => (
    <TouchableOpacity onPress={()=>navigation.navigate('Map Screen')} style={[styles.item, backgroundColor,]}>
    <Image source={{uri:item.photo}}  style={{width:60, height:60,borderRadius:30}} />
    <View style={{justifyContent:"space-evenly",paddingLeft:15}}>
    <Text style={styles.title}>{item.name}</Text>
        <Text style={styles.title2}>{item.Skill}</Text>
        <Rating
            onFinishRating={ratingCompleted}
            style={{alignSelf:'flex-start'}}
            imageSize={18} type='custom' tintColor={coloure}
        />
      </View>
      
    </TouchableOpacity>
  );

  const [selectedId, setSelectedId] = useState(null);
  const renderItem = ({ item, colour }) => {
   
    const backgroundColor = item.id === selectedId ? "#039be5" : "#ffff";
    const color = item.id === selectedId ? 'white' : 'black';
    coloure =  item.id == selectedId?"#039be5":'#ffFf'
   
    
    return (
      
      <Item
        item={item}
        onPress={() => setSelectedId(item.id)}
        backgroundColor={{ backgroundColor }}
        textColor={{ color }}
      />
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
        extraData={selectedId}
        // initialNumToRender={2}
        // maxToRenderPerBatch={5}
        // refreshing={refreshing}
      >
        
      </FlatList>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor:"#e0e0e0",
  //  marginTop: StatusBar.currentHeight || 0,
    
  },
  item: {
    padding: 15,
    marginVertical: 8,
    marginHorizontal: 16,
    borderRadius:20,
    flexDirection:"row",

  },
  title: {
    fontSize: 18,
    fontWeight:'bold'

  },
  title2: {
    fontSize: 12,
    fontWeight:'bold'

  },
});
