import { View,StatusBar } from "react-native";
import { ScrollView,StyleSheet,Text,TextInput,Image,ActivityIndicator,Animated,Switch  } from "react-native";
import { Boton } from "../Style/Boton";
import { useState,useRef,useCallback  } from "react";
import ImagePikker from "./ImagePikker";
import { firebase } from "./Config";
import { Ionicons } from "@expo/vector-icons";
import { ShowError, ShowSuccess } from "../Style/ErrorMessage";


export const SignUp = ({navigation}) => {
   // const co0lor="green"
   const [isEnabled, setIsEnabled] = useState(false);
   const toggleSwitch = () => setIsEnabled(previousState => !previousState);
   

   const [fullName, setFullname] = useState('');
   const [email, setEmail] = useState('');
   const [pass, setPass] = useState('');
   const [cpass, setCPass] = useState('');
   const [loading, setloading] = useState(false);
   const [Address, setAddress] = useState('');
    const [phone, setPhone] = useState('');
    
    const [eye, setEye] = useState("eye-off");
    const [visible,setVisible]=useState(true)
    const [eye2, setEye2] = useState("eye-off");
    const [visible2, setVisible2] = useState(true)
    const [color, setColor] = useState('gray');
    const [color2,setColor2]=useState('gray');
    const [imge,setImge]=useState("")

   const [fnValid, setFnValid] = useState(null);
   const [lnValid, setLnValid] = useState(null);
   const [emailValid, setEmailValid] = useState(null);
   const [passValid, setPassValid] = useState(null);
   const [cpassValid, setCPassValid] = useState(null);
   const [isValid, setIsValid] = useState(false);
   const emailRegex = /\S+@\S+\.\S+/;
    
   const anim = useRef(new Animated.Value(0));

   const shake = useCallback(() => {
     // makes the sequence loop
     Animated.loop(
       // runs the animation array in sequence
       Animated.sequence([
         // shift element to the left by 2 units
         Animated.timing(anim.current, {
           toValue: -10,
             duration: 50,
             useNativeDriver: true
         }),
         // shift element to the right by 2 units
         Animated.timing(anim.current, {
           toValue: 10,
             duration: 50,
             useNativeDriver: true
         }),
         // bring the element back to its original position
         Animated.timing(anim.current, {
           toValue: 0,
             duration: 50,
             useNativeDriver: true
         }),
       ]),
       // loops the above animation config 2 times
       { iterations: 2 }
     ).start();
   }, []);

    
    const showpass=()=>{
        if(eye=="eye-off"){
          setEye("eye")
            setColor("green")
            setVisible(false)
        }else if(eye=="eye"){
          setEye("eye-off")
            setColor("gray")
            setVisible(true)
        }
    }
    const showpass2=()=>{
        if(eye2=="eye-off"){
          setEye2("eye")
            setColor2("green")
            setVisible2(false)
        }else if(eye2=="eye"){
          setEye2("eye-off")
            setColor2("gray")
            setVisible2(true)
        }
      }
    if (loading) {
        return <View style={{ justifyContent: 'center',flex:1 }}>
        <ActivityIndicator size="large" color="#00ff00"  />
        </View>
    }
    
    const validateEmail = () => {
        if (emailRegex.test(email)) {
          setIsValid(true);
        } else {
          setIsValid(false);
          setEmailValid('Please enter a valid email!');
        }
      };
    const CreateUserAccount = () => {
      if (email == "" && pass == ""&&fullName==""&&cpass=="") {
        ShowError('Please fill out all required fields')
        return false
    }

        if (fullName == "" || email == "" ||pass == "" || cpass == "") {
            
            
            if (fullName == "") {
           //     setFnValid("*Required");
               
                shake();
                ShowError('Please inter your Fullname')
                return false     
                
            }
        if (email == "") {
         //   setEmailValid("*Required");
            shake();
            ShowError('Please inter your Email')
            return false
        }
            
        if (pass=="") {
          //  setPassValid("*Required");
            shake();
           ShowError('Please inter your Password')
                return false
          
        }
        if (cpass==''){
          //  setCPassValid("*Required");
            shake();
            ShowError('Please inter your Confirm Password')
            return false
            }
            
        
    }else if (pass !== cpass) {
          //  setCPassValid("*Password did not matched");
            shake();
            ShowError('Password not matched')
            return false
    }
    else {
                try {
                    setloading(true)
                    
                    firebase.auth().createUserWithEmailAndPassword(email, pass).then((response) => {
                            const uid = response.user.uid
                            const data = {
                                id: uid,
                                email,
                                fullName,
                                phone,
                                Address
                            };
                            firebase.firestore().collection('Users').doc(uid).set(data).then(() => {
                                
                                navigation.navigate('Login')
                                ShowSuccess('Signup successfuly')
                                return false
                                
                            })
                            .catch((error) => {
                              ShowError(error.toString())
                              setloading(false);
                              return false
                            });
                    })
                    .catch((error) => {
                      ShowError(error.toString())
                        setloading(false);
                        return false
                        // if (pass.length < 7) {
                        //     setPassValid("Password atleast 6 character")
                        // } else {
                        //     setEmailValid("The email address is badely formated")
                        // }
                    });

                    setloading(false)
                        
            
            } catch (error) {
              ShowError(error.toString())
              setloading(false);
              return false
            }
        
        }
    
    }
      
        
        
                    
    return (
        <View style={styles.container}>
        <StatusBar barStyle='dark-content' />
        <View style={styles.SwitchStyle}>
          <Switch
            trackColor={{ false: "#767577", true: "#1976d2" }}
            thumbColor={isEnabled ? "#fff" : "#f4f3f4"}
            ios_backgroundColor="#aeaeae"
            onValueChange={toggleSwitch}
            value={isEnabled}
        />
          </View>
            <Text style={{fontSize: 35,marginTop:20,marginLeft:20, color:"#1976d2",fontWeight:"bold"}}>Sign UP</Text>
            <ScrollView >
          <View style={styles.margin}>
            <Image style={{width:120,height:120,borderRadius:120/2}}source={require('../assets/Person.png')}/>
                    {/* <ImagePikker img={fullName}/> */}
                </View>
                <View style={{padding:30,marginTop:40}}>
                    <TextInput
                 style={{fontSize: 16, color: 'black', borderBottomColor:fullName==""?"#ddd":color||color, borderBottomWidth:1,paddingBottom:10}}
                        placeholder="Enter Name"
                       
                        onChangeText={t1 => {
                            setFullname(t1)
                            setFnValid("");
                        }}

                        //onPress={(fullName)=>{fullName==""?setColor("red"):setColor("blue")}}
                       // onPress={text==""?error:"#ddd"}

                    />
                    <Text style={{color:"red"}}>{fnValid}</Text>
                </View>
                <View style={{paddingLeft:30,paddingRight:30,paddingBottom:30}}>
                 <TextInput
                style={{ fontSize: 16, color: 'black', borderBottomColor:email==""?"#ddd":'green', borderBottomWidth:1,paddingBottom:10}}
                        placeholder="Email"
                        keyboardType='email-address'
                        onChangeText={t4 => {
                            setEmail(t4)
                            setEmailValid("");
                        }}
                        onChange={validateEmail}
                        
                    />
                    <Text style={{color:"red"}}>{emailValid}</Text>
                </View>
                <View style={{paddingLeft:30,paddingRight:30,paddingBottom:30}}>
                <TextInput
                style={{ fontSize: 16, color: 'black', borderBottomColor:pass==""?"#ddd":'green', borderBottomWidth:1,paddingBottom:10}}
                        placeholder="Password"
                        secureTextEntry={visible}
                        
                        onChangeText={t5 => {
                            setPass(t5)
                            setPassValid("");
                        }}
                    />
                    <View style={{marginLeft:Platform.OS === 'ios' ?295:310,marginTop:-35}}>
                    <Ionicons onPress={showpass} name={eye} size={20} color={color} />
                </View>
                    <Text style={{color:"red",marginTop:6.5}}>{passValid}</Text>
                </View>
                <View style={{paddingLeft:30,paddingRight:30,paddingBottom:30}}>
                <TextInput
                style={{ fontSize: 16, color: 'black', borderBottomColor:cpass==""?"#ddd":'green', borderBottomWidth:1,paddingBottom:10}}
                        placeholder="Confirm Password"
                        autoFocus={false}
                        secureTextEntry={visible2}
                        onChangeText={t6 => {
                            setCPass(t6)
                            setCPassValid("");
                        }}
                    />
                    <View style={{marginLeft:Platform.OS === 'ios' ?295:310,marginTop:-35}}>
                    <Ionicons  name={eye2} onPress={showpass2} size={20} color={color2} />
                </View>
                    <Text style={{color:"red",marginTop:6.5}}>{cpassValid}</Text>
                </View>
                   <View style={styles.margin}>
                   <Animated.View style={{ transform: [{ translateX: anim.current }] }}>
                    <Boton onPress={CreateUserAccount} title={"Sign Up"} widt={200} />
                    </Animated.View>
                </View>
                <View style={{height:250}}></View>
        </ScrollView>
        </View>
    )


}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "#fff"
    
      
    },
    margin: {
        marginTop: 45,
        justifyContent: "center",
        alignItems:"center"
    },
    SwitchStyle: {
      flex: 1,
      alignItems: 'flex-end',
      marginTop: 15,
      marginRight:20
    }

   
  });
