import react, { useState } from "react";
import {SafeAreaView,BackHandler, Alert, View } from "react-native";
import { Slider } from "./Slider";
import React, { useEffect } from "react";
import { BoxHelper } from "../../Style/BoxHelper";
import { ScrollView,Text,Image,StyleSheet } from "react-native";
import SearchBar from "react-native-dynamic-search-bar";
import AsyncStorage from '@react-native-async-storage/async-storage';
import { NativeScreenNavigationContainer } from "react-native-screens";


export const FrontScreen = ({user,navigation,...props}) => {
  
  

  
  

  useEffect( async () => {

    let userToken;
    userToken = null;
    try {
      userToken = await AsyncStorage.getItem('xyz')
      console.log("hello its front screen",userToken);
      } catch (e) {
      console.log(e);
    }
  })



    const backAction = () => {
        Alert.alert("Hold on!", "Are you sure you want to close this application?", [
          {
            text: "Cancel",
            onPress: () => null,
            style: "cancel"
          },
          { text: "YES", onPress: () => BackHandler.exitApp() }
        ]);
        return true;
      };
    
      useEffect(() => {
        BackHandler.addEventListener("hardwareBackPress", backAction);
    
        return () =>
          BackHandler.removeEventListener("hardwareBackPress", backAction);
      }, []);
    
    
    return (
        <ScrollView  showsVerticalScrollIndicator={false}>
        <View style={{}}>  
        <SearchBar
            style={{marginTop:100,width:"95%"}}
            fontColor="#c6c6c6"
            iconColor="#c6c6c6"
            shadowColor="#282828"
            cancelIconColor="#c6c6c6"
          //  backgroundColor="#353d5e"
            placeholder="Search here"
            cancelIconColor="#fdfdfd"
        //  onChangeText={(text) =>filterList(text)}
            onSearchPress={() => console.log("Search Icon is pressed")}
         //  onClearPress={(text) => filterList(text)}
        //   onPress={() => alert("onPress")}
          />
          </View>  
              <View >
              <Slider />
              </View>
              <Text onPress={props.onPress} style={{ color: "gray",fontWeight:'bold',fontSize:14,marginLeft:5,marginTop:10,marginBottom:10}}>RECENTS</Text>
              <ScrollView horizontal={true}
              showsHorizontalScrollIndicator={false}
              style={{height:55}}
            >
                <Image style={styles.imgStyle} source={require("../../assets/HelperImageIcon/carrepair.png")}/>
                <Image style={styles.imgStyle} source={require("../../assets/HelperImageIcon/electrician.png")} />
                <Image style={styles.imgStyle} source={require("../../assets/HelperImageIcon/plumber.png")} />
                <Image style={styles.imgStyle} source={require("../../assets/HelperImageIcon/insurance.png")} />
                <Image style={styles.imgStyle} source={require("../../assets/HelperImageIcon/teacher.png")} />
                <Image style={styles.imgStyle} source={require("../../assets/HelperImageIcon/fridge.png")} />
         
  
        </ScrollView >
       
            <Text style={{ color: "gray",fontWeight:'bold',fontSize:14,marginLeft:5 ,marginTop:20}}>ALL SERVICES</Text>
              
        <View style={{ flexDirection: 'row', justifyContent: 'center' }}>
                <BoxHelper onPress={() => { navigation.navigate('Helpers') }} HelperType={'Electrician'} imageUri={require("../../assets/HelperImageIcon/electrician.png")}/>
                 <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Plumber' }) }} HelperType={'Plumber'} imageUri={require("../../assets/HelperImageIcon/plumber.png")} /> 
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Car Repair' }) }} HelperType={'Car Repair'} imageUri={require("../../assets/HelperImageIcon/carrepair.png")}/> 
                    
                </View>
                <View style={{flexDirection:'row',justifyContent:'center',marginTop:-1}}>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Insurance' }) }} HelperType={'Insurance'} imageUri={require("../../assets/HelperImageIcon/insurance.png")}/>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Doctor' }) }} HelperType={'Doctor'} imageUri={require("../../assets/HelperImageIcon/doctor.png")}/>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Tutor Service' }) }} HelperType={'Tutor Service'} imageUri={require("../../assets/HelperImageIcon/teacher.png")}/>
                 </View>
                 
                <View style={{flexDirection:'row',justifyContent:'center',marginTop:-1}}>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Ambulance' }) }} HelperType={'Ambulance'} imageUri={require("../../assets/HelperImageIcon/ambulance.png")}/>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Fridge Repair' }) }} HelperType={'Fridge Repair'} imageUri={require("../../assets/HelperImageIcon/fridge.png")}/>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Home Repair' }) }} HelperType={'Home Repair'} imageUri={require("../../assets/HelperImageIcon/homereapir.png")}/>
                </View>
            
                
                <View style={{flexDirection:'row',justifyContent:'center',marginTop:-1}}>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Ambulance' }) }} HelperType={'Ambulance'} imageUri={require("../../assets/HelperImageIcon/ambulance.png")}/>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Refrigerator Repair' }) }} HelperType={'Refrigerator Repair'} imageUri={require("../../assets/HelperImageIcon/fridge.png")}/>
                <BoxHelper onPress={() => { navigation.navigate('Helpers',{ name: 'Home Repair' }) }} HelperType={'Home Repair'} imageUri={require("../../assets/HelperImageIcon/homereapir.png")}/>
                </View>

        </ScrollView>

    );
}
const styles = StyleSheet.create({
    imgStyle: {
        height: 50,
        width: 50,
        borderRadius: 50 / 2,
        borderWidth: 1.5,
        borderColor: "gray",
        marginLeft: 10,
        
}


})
