import React, { useState, useRef } from "react";
import { firebase } from "../firebaseConfig/Config";
import {
  Keyboard,
  KeyboardAvoidingView,
  Text,
  TextInput,
  TouchableWithoutFeedback,
  Pressable,
  View,
  StatusBar,
  Switch,
  TouchableOpacity,
  ActivityIndicator,
  Platform
} from "react-native";
import * as Location from "expo-location";
import Geocoder from "react-native-geocoding";
import RNPickerSelect from "react-native-picker-select";
import { Button, SocialIcon } from "react-native-elements";
import * as Facebook from "expo-facebook";
import { StyleSheet } from "react-native";
import { ShowError, ShowSuccess } from "../../Style/ErrorMessage";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Spinner from "react-native-loading-spinner-overlay";
import { color } from "react-native-elements/dist/helpers";
import GoogleApi from "../Mapsource/GoogleApi";
import { Ionicons } from "@expo/vector-icons";

export default function SignupScreen({ navigation }) {
  const [fullName, setFullname] = useState("");
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("");
  const [cpass, setCPass] = useState("");

  const [loading, setloading] = useState(false);
  const [isEnabled, setIsEnabled] = useState(false);
  const toggleSwitch = () => setIsEnabled((previousState) => !previousState);
  const [eye, setEye] = useState("eye-off");
  const [eye2, setEye2] = useState("eye-off");
  const [color, setColor] = useState("gray");
  const [color2, setColor2] = useState("gray");
  const [visible, setVisible] = useState(true);
  const [visible2, setVisible2] = useState(true);
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");
  const [Address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [skills, setSkills] = useState("");
  const [loadings, setLoadings] = useState(false);
  const [placeholder, setPlaceHolder] = useState({
    label: "Select Your Skills...",
    value: null,
    color: "#9EA0A4",
  });

  const CustomerSignup = () => {
    if (email == "" && pass == "" && fullName == "" && cpass == "") {
      ShowError("Please fill out all required fields");
    } else if (fullName == "" || email == "" || pass == "" || cpass == "") {
      if (fullName == "") {
        ShowError("Please inter your Fullname");
        return false;
      }
      if (email == "") {
        ShowError("Please inter your Email");
        return false;
      }

      if (pass == "") {
        ShowError("Please inter your Password");
        return false;
      }
      if (cpass == "") {
        ShowError("Please inter your Confirm Password");
        return false;
      }
    } else if (pass !== cpass) {
      ShowError("Password not matched");
      return false;
    } else {
      try {
        setloading(true);

        firebase
          .auth()
          .createUserWithEmailAndPassword(email, pass)
          .then((response) => {
            const uid = response.user.uid;
            const data = {
              id: uid,
              email,
              fullName,
              phone,
              Address,
              lat,
              long,
            };
            firebase
              .firestore()
              .collection("Users")
              .doc(uid)
              .set(data)
              .then(() => {
                navigation.replace("Login");
                ShowSuccess("Signup successfuly");
                setloading(false);
              })
              .catch((error) => {
                ShowError(error.toString());
                setloading(false);
              });
          })
          .catch((error) => {
            ShowError(error.toString());
            setloading(false);
          });
      } catch (error) {
        ShowError(error.toString());
        setloading(false);
        return false;
      }
    }
  };
  const HelperSignup = () => {
    if (email == "" && pass == "" && fullName == "" && cpass == "") {
      ShowError("Please fill out all required fields");
    } else if (fullName == "" || email == "" || pass == "" || cpass == "") {
      if (fullName == "") {
        ShowError("Please inter your Fullname");
        return false;
      }
      if (email == "") {
        ShowError("Please inter your Email");
        return false;
      }
      if (skills == "") {
        ShowError("Please select your skill");
        return false;
      }
      if (Address == "") {
        ShowError("Please get your Address");
        return false;
      }
      if (pass == "") {
        ShowError("Please inter your Password");
        return false;
      }
     
      if (cpass == "") {
        ShowError("Please inter your Confirm Password");
        return false;
      }
    } else if (pass !== cpass) {
      ShowError("Password not matched");
      return false;
    } else {
      try {
        setloading(true);

        firebase
          .auth()
          .createUserWithEmailAndPassword(email, pass)
          .then((response) => {
            const uid = response.user.uid;
            const data = {
              id: uid,
              email,
              fullName,
              phone,
              Address,
              lat,
              long,
              skills,
              count,
              star,
              sum
            };
            firebase
              .firestore()
              .collection("Helpers")
              .doc(uid)
              .set(data)
              .then(() => {
                navigation.replace("Login");
                ShowSuccess("Signup successfuly");
                setloading(false);
              })
              .catch((error) => {
                ShowError(error.toString());
                setloading(false);
              });
          })
          .catch((error) => {
            ShowError(error.toString());
            setloading(false);
          });
      } catch (error) {
        ShowError(error.toString());
        setloading(false);
        return false;
      }
    }
  };
  const showpass = () => {
    if (eye == "eye-off") {
      setEye("eye");
      setColor("#3897f1");
      setVisible(false);
    } else if (eye == "eye") {
      setEye("eye-off");
      setColor("gray");
      setVisible(true);
    }
  };
  const showpass2 = () => {
    if (eye2 == "eye-off") {
      setEye2("eye");
      setColor2("#3897f1");
      setVisible2(false);
    } else if (eye2 == "eye") {
      setEye2("eye-off");
      setColor2("gray");
      setVisible2(true);
    }
  };
  const getLocation = async () => {
    console.log("hello its me");
    setLoadings(true);
    try {
      const { granted } = await Location.requestForegroundPermissionsAsync();
      if (!granted) return;
      const {
        coords: { latitude, longitude },
      } = await Location.getCurrentPositionAsync();

      setLat(latitude);
      setLong(longitude);
      Geocoder.init(GoogleApi.Google_Api);
      Geocoder.from(latitude, longitude).then((json) => {
        var address_Component = json.results[1].formatted_address;
        console.log(address_Component);
        setAddress(address_Component);
        setLoadings(false);
      });
    } catch (error) {
      console.log(error);
    }
  };
  const hello = () => {
    console.log("hello its me");
  }

  return (
    <KeyboardAvoidingView style={styles.containerView} behavior="padding">
      <StatusBar barStyle="dark-content" />
      <View style={styles.SwitchStyle}>
        <Switch
          trackColor={{ false: "#767577", true: "#1976d2" }}
          thumbColor={isEnabled ? "#fff" : "#f4f3f4"}
          ios_backgroundColor="#aeaeae"
          onValueChange={toggleSwitch}
          value={isEnabled}
        />
      </View>
      <View style={styles.container}>
        <Spinner
          //visibility of Overlay Loading Spinner
          visible={loading}
          //Text with the Spinner
          textContent={"Loading..."}
          //Text style of the Spinner Text
          textStyle={styles.spinnerTextStyle}
        />
        <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
          <View style={styles.loginScreenContainer}>
            <View style={styles.loginFormView}>
              <Text style={styles.logoText}>Signup</Text>

              <TextInput
                placeholder="Full name"
                placeholderTextColor={"#c4c3cb"}
                style={styles.loginFormTextInput}
                keyboardType="email-address"
                onChangeText={(t1) => {
                  setFullname(t1);
                }}
              />

              <TextInput
                placeholder="Email"
                placeholderTextColor={"#c4c3cb"}
                style={styles.loginFormTextInput}
                keyboardType="email-address"
                onChangeText={(t1) => {
                  setEmail(t1);
                }}
              />
              {isEnabled ? (
                <>
                  <View>
                    <RNPickerSelect
                      placeholder={placeholder}
                      label="Select a sportdfgdfgdg bgd..."
                      onValueChange={(skil) => setSkills(skil)}
                      items={[
                        { label: "Electrician", value: "Electrician" },
                        { label: "Plumber", value: "Plumber" },
                        { label: "Car Repair", value: "Car Repair" },
                        { label: "Doctor", value: "Doctor" },
                        {
                          label: "Refrigerator Repair",
                          value: "Refrigerator Repair",
                        },
                        { label: "Tutor Service", value: "Tutor Service" },
                        { label: "Insurance", value: "Insurance" },
                        { label: "Ambulance", value: "Ambulance" },
                        { label: "Home Repair", value: "Home Repair" },
                      ]}
                      style={pickerSelectStyles}
                    />
                  </View>
                  <View style={{ flexDirection: "row" }}>
                    <TextInput
                      placeholder="Address"
                      placeholderTextColor={"#c4c3cb"}
                      value={Address}
                      style={styles.loginFormTextInput2}
                      onChangeText={(t1) => {
                        setAddress(t1);
                      }}
                    />
                    {loadings ?<View style={{ marginLeft: -38, marginTop: 16,width:35,backgroundColor:"#fafafa",height:30 }}>
                    <ActivityIndicator size="small" color="gray" visible={loadings} />
                  </View>
                       : <TouchableOpacity onPress={() => getLocation()}>
                       <Ionicons
                          style={{ marginLeft:-38, marginTop: 13,backgroundColor:"#fafafa"}}
                          name="location"
                          size={27}
                          color={"gray"}
                        />
                      </TouchableOpacity>
                    
                    }
                  </View>
                </>
              ) : null}

              <View style={{ flexDirection: "row" }}>
                <TextInput
                  placeholder="Password"
                  placeholderTextColor={"#c4c3cb"}
                  style={styles.loginFormTextInput2}
                  secureTextEntry={visible}
                  onChangeText={(t1) => {
                    setPass(t1);
                  }}
                />

                <Ionicons
                  style={{ marginLeft: -35, marginTop: 15 }}
                  onPress={showpass}
                  name={eye}
                  size={22}
                  color={color}
                />
              </View>
              <View style={{ flexDirection: "row" }}>
                <TextInput
                  placeholder="Confirm Password"
                  placeholderTextColor={"#c4c3cb"}
                  style={styles.loginFormTextInput2}
                  secureTextEntry={visible2}
                  onChangeText={(t2) => {
                    setCPass(t2);
                  }}
                />

                <Ionicons
                  style={{ marginLeft: -35, marginTop: 15 }}
                  onPress={showpass2}
                  name={eye2}
                  size={22}
                  color={color2}
                />
              </View>

              {isEnabled ? (
                <Button
                  buttonStyle={styles.loginButton}
                  onPress={() => HelperSignup()}
                  title="Helper Signup"
                />
              ) : (
                <Button
                  buttonStyle={styles.loginButton}
                  onPress={() => CustomerSignup()}
                  title="Signup"
                />
              )}
              <TouchableOpacity onPress={() => navigation.replace("Login")}>
                <Text style={styles.createNewAccount}>
                  Already have account?
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </TouchableWithoutFeedback>
      </View>
    </KeyboardAvoidingView>
  );
}
